#include <stdlib.h>
#include <stdio.h>

int main(void)
{
    char board[3][3] =
        {
            {'O', 'O', 'X'},
            {'X', 'O', 'O'},
            {'X', 'O', 'X'}};

    for (int row = 0; row < 3; row++)
    {
        for (int col = 0; col < 3; col++)
        {
            printf("%c", board[row][col]);
        }
        printf("\n");
    };

    for (int row = 0; row < 3; row++)
    {
        for (int col = 0; col < 3; col++)
        {
            printf("%c ", board[row][col]);
        }
        printf("\n");
    }

    for (int row = 0; row < 3; row++)
    {
        for (int col = 0; col < 3; col++)
        {
            printf("%c  ", board[row][col]);
        }
        printf("\n");
    }
}

