#include <stdlib.h>
#include <stdio.h>

int main(void)
{
    float base = 3.5;
    float height = 4.85;
    printf("Input Value (Base/Height):\n");
    scanf("%f %f", &base, &height);

    float area = 0.5 * base * height;
    printf("%f \n", area);
    return 0;
}

