#include <stdlib.h>
#include <stdio.h>

int main(void)
{
  float mileKilometers = 1.60934;
  float userInput = 0;
  printf("Mile to Kilometer Converter\n");
  printf("Enter Miles: ");
  scanf("%f", &userInput);

  float kilo = userInput * mileKilometers;

  printf("Miles To Kilometers: %f \n", kilo);
  printf("\n");
  return 0;
}




