#include <stdlib.h>
#include <stdio.h>
#include <math.h>

int main(void)
{
    double ladderHeight, radians, ladderLength, angleDegree;

    printf("Input Ladder Length:\n");
    scanf("%lf", &ladderLength);

    printf("Input Angle of Ladder from Ground:\n");
    scanf("%lf", &angleDegree);
    radians = angleDegree * M_PI / 180;
    ladderHeight = ladderLength * sin(radians);
    printf("Current Height of Ladder is: %.2lf", ladderHeight);
    printf("\n");
    return 0;
}

