#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main(void)
{
    int dealerNum, playerNum, min, max, randomNum, dealerDist, playerDist, totalGames, playerWins, dealerWins;

    srand(time(NULL)); // seeds random generator with unix timecode

    min = 0; // Minimum: 0

    max = 100; // Maximum: 100

    // randomNum = 21;

    totalGames = 0; // Set the total games counter to zero.

    playerWins = 0; // Set the player wins counter to zero.

    dealerWins = 0; // Set the dealer wins counter to zero.
    printf("\n");
    printf("Player vs Dealer: Guessing Game\n");

    do // Repeat:
    {
        randomNum = (rand() % (max - min + 1)) + min; // Generate a random whole number from zero through one hundred.
        do                                            // Ensure dealerNum is not randomNum (Fair Odds)
        {
            dealerNum = (rand() % (max - min + 1)) + min; // Start the random number generator.
        } while (dealerNum == randomNum);

        do
        {
            printf("\n");
            printf("Game : %d\n", totalGames + 1);
            printf("Choose a number from 0 to 100: (input %d to exit)\n", randomNum); // Ask the player to choose a whole number from zero through one hundred.
            if (scanf("%d", &playerNum) != 1)                                         // Validate input type
            {
                printf("error: invalid character\n");
                scanf("%*s"); // Clear invalid input from buffer
                printf("\n");
                continue;
            }

            if (playerNum < 0 || playerNum > 100) // Validate input range
            {

                printf("error: out of range\n");
                printf("\n");
                continue;
            }
            break;
        } while (1);

        printf("\n");
        dealerDist = abs(dealerNum - randomNum); // Compute the dealer’s distance from twenty-one using absolute difference.
        playerDist = abs(playerNum - randomNum); // Compute the player’s distance from twenty-one using absolute difference.

        if (playerNum != randomNum) // handles edge case to exit program counting as player win
        {
            printf("You Chose %d!\n", playerNum);
            printf("Dealer Chose: %d\n", dealerNum); // Show the dealer’s number.
            totalGames++;
            // Increment the total games counter by one.
            if (playerDist < dealerDist) // If the player’s distance is smaller than the dealer’s distance
            {
                printf("%d is closer to %d\n", playerNum, randomNum); // tell the player they win.
                printf("You Win!\n");
                playerWins++; // tell the player they win.
            }
            else if (playerDist > dealerDist) // If the player’s distance is larger than the dealer’s distance
            {
                printf("%d is closer to %d\n", dealerNum, randomNum); // tell the player the dealer wins.
                printf("Dealer Wins!\n");
                dealerWins++; // tell the player the dealer wins.
            }
            else // If the distances are equal
            {
                printf("Since you both chose: %d\n", playerNum);
                printf("It's a Tie!\n"); // say it is a tie.
            }
        }
        else
        {
            // printf("Confirm: y/n", playerNum);
            printf("Exiting Game...\n");
            printf("\n"); // After the loop print summary:
            printf("Game Summary:\n");
            printf("%d Games Total\n", totalGames);
            playerWins == 1 ? printf("You Won: %d time,\n", playerWins) : printf("You Won: %d times,\n", playerWins);
            dealerWins == 1 ? printf("Dealer Won: %d time,\n", dealerWins) : printf("Dealer Won: %d times,\n", dealerWins);
            // printf("You Won: %d Time\\s,\n", playerWins);  // should I use terniery operator here to correct grammer of "time/s"?
            // printf("Dealer Won: %d Time\\s,\n", dealerWins); // should I use terniery operator here to correct grammer of "time/s"?
            printf("You Are: %d for %d.\n", playerWins, totalGames); // Total games, dealer wins, player wins, “You’re playerWins/totalGames.”
            printf("\n");
            printf("Goodbye!\n"); //// Say goodbye with the closing message from the prompt.
            printf("\n");
        }
    } while (playerNum != randomNum); // Until the player chooses to exit by entering randomNum.
    return 0;
}

