{
    int userAge;

    // Read age with validation: must be an integer >= 0
    for (;;) {
        printf("Enter your age: ");
        int rc = scanf("%d", &userAge);
        if (rc == 1 && userAge >= 0) {
            break; // valid input
        }
        printf("Invalid age. Please enter a non-negative integer.
");
        // Clear the rest of the line to avoid infinite loop on bad input
        int ch;
        while ((ch = getchar()) != '
' && ch != EOF) {
