#include <stdio.h>
#include <stdlib.h>
#include <string.h>

typedef struct HorseEntry
{
    char horse_name[50];
    char jockey_name[50];
    struct HorseEntry *next;
} *HorseEntryPtr;

typedef struct Race
{
    HorseEntryPtr entries;
} Race;

HorseEntryPtr addHorse(HorseEntryPtr head, const char *horse, const char *jockey)
{
    HorseEntryPtr n = malloc(sizeof(struct HorseEntry));
    if (!n)
    {
        perror("Memory allocation failed");
        exit(EXIT_FAILURE);
    }
    strcpy(n->horse_name, horse);
    strcpy(n->jockey_name, jockey);
    n->next = NULL;

    if (!head)
        return n;

    HorseEntryPtr curr = head;
    while (curr->next)
        curr = curr->next;
    curr->next = n;
    return head;
}

void printRaces(Race races[], int numRaces)
{
    for (int i = 0; i < numRaces; i++)
    {
        printf("RACE %d:\n", i + 1);
        HorseEntryPtr curr = races[i].entries;
        while (curr)
        {
            printf("Jockey: %s\nHorse: %s\n\n", curr->jockey_name, curr->horse_name);
            curr = curr->next;
        }
        printf("\n");
    }
}

int main(void)
{
    Race races[4];
    printf("\n");
    for (int i = 0; i < 4; i++)
        races[i].entries = NULL;

    races[0].entries = addHorse(races[0].entries, "LowPoints", "Ima Fused");
    races[0].entries = addHorse(races[0].entries, "Misunderstood", "Grading Frustration");

    races[1].entries = addHorse(races[1].entries, "ChatHelper", "AI Guidance");
    races[1].entries = addHorse(races[1].entries, "Vague", "Sue Fley");

    races[2].entries = addHorse(races[2].entries, "RepoMess", "Workflow Woes");
    races[2].entries = addHorse(races[2].entries, "FolderChaos", "Ann Teague");

    races[3].entries = addHorse(races[3].entries, "Efficient", "Better Practices");

    printRaces(races, 4);

    return 0;
}

