int exacta(float cost, float *balance, int payout, int betOrder[2], int raceResults[])
{
    *balance -= cost;
    if (betOrder[0] == raceResults[0] && betOrder[1] == raceResults[1])
    {
        *balance += payout;
        printf("You Won! New Balance: $%.2f\n", *balance);
        return 1;
    }
    printf("You Lost. New Balance: $%.2f\n", *balance);
    return 0;
}

int exactabox(float cost, float *balance, int payout, int betOrder[2], int raceResults[])
{
    *balance -= cost;
    if ((betOrder[0] == raceResults[0] && betOrder[1] == raceResults[1]) ||
        (betOrder[0] == raceResults[1] && betOrder[1] == raceResults[0]))
    {
        *balance += payout;
        printf("You Won! New Balance: $%.2f\n", *balance);
        return 1;
    }
    printf("You Lost. New Balance: $%.2f\n", *balance);
    return 0;
}

int trifecta(float cost, float *balance, int payout, int betOrder[3], int raceResults[])
{
    *balance -= cost;
    if (betOrder[0] == raceResults[0] && betOrder[1] == raceResults[1] && betOrder[2] == raceResults[2])
    {
        *balance += payout;
        printf("You Won! New Balance: $%.2f\n", *balance);
        return 1;
    }
    printf("You Lost. New Balance: $%.2f\n", *balance);
    return 0;
}

int trifectabox(float cost, float *balance, int payout, int betOrder[3], int raceResults[])
{
    *balance -= cost;
    int win[3] = {raceResults[0], raceResults[1], raceResults[2]};
    int matches = 0;
    for (int i = 0; i < 3; i++)
    {
        for (int j = 0; j < 3; j++)
        {
            if (betOrder[i] == win[j])
            {
                matches++;
                win[j] = -1; // mark as used
                break;
            }
        }
    }
    if (matches == 3)
    {
        *balance += payout;
        printf("You Won! New Balance: $%.2f\n", *balance);
        return 1;
    }
    printf("You Lost. New Balance: $%.2f\n", *balance);
    return 0;
}

