#define MAX_HISTORY 100
#define START_USD 75000

// ---------- STRUCTS ----------
typedef struct
{
    char ticker[4];
    double btc;
    double usd;
} Wallet;

typedef struct
{
    char entries[MAX_HISTORY][200];
    int count;
} Ledger;

// ---------- ENUMS ----------
// ⚡ NEW: Added enums for explicit trade type and amount type
typedef enum
{
    BUY,
    SELL
} TradeType; // ⚡ NEW
typedef enum
{
    AMOUNT_BTC,
    AMOUNT_USD
} AmountType; // ⚡ NEW

// ---------- UTIL ----------
int getLivePrice(void)
{
    return (rand() % (35000 - 25000 + 1)) + 25000;
}

// ⚡ CHANGE: Added size parameter for safe snprintf usage
void getTimestamp(char *buffer, size_t size) // ⚡ CHANGE
{
    time_t now = time(NULL);
    struct tm *t = localtime(&now);                  // ⚡ NEW: localtime used to convert time_t to calendar time
    strftime(buffer, size, "%B %d, %Y %H:%M:%S", t); // ⚡ CHANGE: safe formatting
}

// ---------- WALLET ----------
void printBalance(Wallet *w, int price)
{
    // ⚡ CHANGE: Included ticker in output
    printf(
        "%s | USD: $%.2f | BTC: %.4f | Value: $%.2f\n", // ⚡ CHANGE
        w->ticker,                                      // ⚡ CHANGE
        w->usd,
        w->btc,
        w->btc * price);

    printf("Exit? Y/N: ");

    char response[10];
    sscanf(response, "%9s", response);
    fgets(response, sizeof(response), stdin);
    if (response[0] == 'Y' || response[0] == 'y')
        return 0;
}

// ---------- LEDGER ----------
void addHistory(Ledger *l, const char *msg)
{
    // ⚡ CHANGE: use snprintf instead of strcpy to prevent overflow
    if (l->count < MAX_HISTORY)
        snprintf(l->entries[l->count++], 200, "%s", msg); // ⚡ CHANGE
}

void printHistory(Ledger *l)
{
    if (l->count == 0)
    {
        printf("No transactions.\n");
        return;
    }
    for (int i = 0; i < l->count; i++)
        printf("%s\n", l->entries[i]);
}

// ---------- TRADING CORE ----------
// ⚡ NEW: Single trade function replaces all duplicated buy/sell logic
void trade(
    Wallet *w,
    Ledger *l,
    TradeType tradeType,   // ⚡ NEW
    AmountType amountType, // ⚡ NEW
    double value,
    int price)
{
    double btcAmount;

    // ⚡ CHANGE: explicit USD vs BTC calculation
    btcAmount = (amountType == AMOUNT_USD) ? value / price : value; // ⚡ CHANGE

    if (tradeType == BUY)
    {
        if (w->usd < btcAmount * price)
        {
            printf("Not enough USD.\n");
            return;
        }
        w->usd -= btcAmount * price;
        w->btc += btcAmount;
    }
    else
    {
        if (w->btc < btcAmount)
        {
            printf("Not enough BTC.\n");
            return;
        }
        w->btc -= btcAmount;
        w->usd += btcAmount * price;
    }

    char timebuf[64], log[200];
    getTimestamp(timebuf, sizeof(timebuf)); // ⚡ CHANGE: pass size
    // ⚡ CHANGE: unified log format for buy/sell
    snprintf(
        log,
        sizeof(log),
        "%s %.4f BTC for %s on %s",           // ⚡ CHANGE
        tradeType == BUY ? "Bought" : "Sold", // ⚡ CHANGE
        btcAmount,
        timebuf);
    addHistory(l, log);
}

// ---------- WRAPPERS ----------
// ⚡ NEW: thin wrappers for explicit command handling
void buyBTC(Wallet *w, Ledger *l, double btc, int price) { trade(w, l, BUY, AMOUNT_BTC, btc, price); }   // ⚡ NEW
void buyUSD(Wallet *w, Ledger *l, double usd, int price) { trade(w, l, BUY, AMOUNT_USD, usd, price); }   // ⚡ NEW
void sellBTC(Wallet *w, Ledger *l, double btc, int price) { trade(w, l, SELL, AMOUNT_BTC, btc, price); } // ⚡ NEW
void sellUSD(Wallet *w, Ledger *l, double usd, int price) { trade(w, l, SELL, AMOUNT_USD, usd, price); } // ⚡ NEW

