int main(void)
{
    srand(time(NULL));

    Wallet userWallet = {"BTC", 0.0, START_USD};
    Ledger userLedger = {.count = 0};

    char command[100];

    while (1)
    {
        printf(
            "\nEnter Command:\n");
        printf("\n");
        printf(
            "price\n"
            "buy btc X | buyWith usd Y\n"  // ⚡ CHANGE: explicit commands
            "sell btc X | sellFor usd Y\n" // ⚡ CHANGE: explicit commands
            "balance\n"
            "history\n"
            "exit\n> ");
        printf("\n");

        fgets(command, sizeof(command), stdin);
        command[strcspn(command, "\n")] = 0;

        // ⚡ CHANGE: command parsing now explicit
        if (strcmp(command, "price") == 0)
        {
            int price = getLivePrice();
            printf("Current BTC Price: $%d\n", price);
        }
        else if (strncmp(command, "buy btc ", 8) == 0)
        {
            int price = getLivePrice();
            buyBTC(&userWallet, &userLedger, atof(command + 8), price);
        }
        else if (strncmp(command, "buyWith usd ", 8) == 0)
        {
            int price = getLivePrice();
            buyUSD(&userWallet, &userLedger, atof(command + 8), price);
        }
        else if (strncmp(command, "sell btc ", 9) == 0)
        {
            int price = getLivePrice();
            sellBTC(&userWallet, &userLedger, atof(command + 9), price);
        }
        else if (strncmp(command, "sellFor usd ", 9) == 0)
        {
            int price = getLivePrice();
            sellUSD(&userWallet, &userLedger, atof(command + 9), price);
        }
        else if (strcmp(command, "balance") == 0)
        {
            int price = getLivePrice();
            printBalance(&userWallet, price);
        }
        else if (strcmp(command, "history") == 0)
        {
            printHistory(&userLedger);
        }
        else if (strcmp(command, "exit") == 0)
        {
            break;
        }
        else
        {
            printf("Unknown command.\n");
        }
    }

    return 0;
}

