#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <time.h>

#define MAX_HISTORY 100
#define START_USD 75000
#define SATOSHIS_PER_BTC 100000000LL

// ---------- STRUCTS ----------
typedef struct
{
    char ticker[4];
    double btc;
    double usd;
} Wallet;

typedef struct
{
    char entries[MAX_HISTORY][200];
    int count;
} Ledger;

// ---------- UTIL ----------
int getLivePrice(void)
{
    return (rand() % (35000 - 25000 + 1)) + 25000;
}

void getTimestamp(char *buffer)
{
    time_t now = time(NULL);
    struct tm *t = localtime(&now);
    strftime(buffer, 64, "%Y-%m-%d %H:%M:%S", t);
}

// ---------- LEDGER ----------
void addHistory(Ledger *l, const char *msg)
{
    if (l->count < MAX_HISTORY)
    {
        strncpy(l->entries[l->count++], msg, 199);
        l->entries[l->count - 1][199] = '\0';
    }
}

void printHistory(Ledger *l)
{
    if (l->count == 0)
    {
        printf("No transactions.\n");
        return;
    }
    for (int i = 0; i < l->count; i++)
        printf("%s\n", l->entries[i]);
}

// ---------- WALLET ----------
void printBalance(Wallet *w, int price)
{
    printf("%s | USD: $%.2f | BTC: %.8f | Value: $%.2f\n",
           w->ticker,
           w->usd,
           w->btc,
           w->btc * price);
}

// ---------- TRADING CORE ----------
void trade(Wallet *w, Ledger *l, int isBuy, double btcAmount, int price)
{
    if (btcAmount <= 0)
    {
        printf("Invalid amount.\n");
        return;
    }

    double usdAmount = btcAmount * price;

    if (isBuy)
    {
        if (w->usd < usdAmount)
        {
            printf("Not enough USD.\n");
            return;
        }
        w->usd -= usdAmount;
        w->btc += btcAmount;
    }
    else
    {
        if (w->btc < btcAmount)
        {
            printf("Not enough BTC.\n");
            return;
        }
        w->btc -= btcAmount;
        w->usd += usdAmount;
    }

    long long sats = (long long)(btcAmount * SATOSHIS_PER_BTC);

    char timebuf[64], log[200];
    getTimestamp(timebuf);

    sprintf(log,
            "%s %.8f BTC (%lld sat) @ $%d on %s",
            isBuy ? "Bought" : "Sold",
            btcAmount,
            sats,
            price,
            timebuf);

    addHistory(l, log);

    printf("%s %.8f BTC (%lld sat)\n",
           isBuy ? "Bought" : "Sold",
           btcAmount,
           sats);
}

// ---------- MAIN ----------
int main(void)
{
    srand(time(NULL));

    Wallet wallet = {"BTC", 0.0, START_USD};
    Ledger ledger = {0};

    char input[100];
    int price = getLivePrice();
    int historyCount = 0;

    printf("=== Bitcoin Trading Simulator ===\n");
    printf("Commands:\n");
    printf("price\n");
    printf("buy btc X\n");
    printf("sell btc X\n");
    printf("sellFor usd Y\n");
    printf("balance\n");
    printf("history (twice to exit)\n\n");

    while (1)
    {
        printf("btc> ");
        if (!fgets(input, sizeof(input), stdin))
            break;

        input[strcspn(input, "\n")] = 0;

        if (strcmp(input, "price") == 0)
        {
            price = getLivePrice();
            printf("BTC Price: $%d\n", price);
            historyCount = 0;
        }
        else if (strncmp(input, "buy btc ", 8) == 0)
        {
            double btc;
            if (sscanf(input + 8, "%lf", &btc) == 1)
                trade(&wallet, &ledger, 1, btc, price);
            historyCount = 0;
        }
        else if (strncmp(input, "sell btc ", 9) == 0)
        {
            double btc;
            if (sscanf(input + 9, "%lf", &btc) == 1)
                trade(&wallet, &ledger, 0, btc, price);
            historyCount = 0;
        }
        else if (strncmp(input, "sellFor usd ", 12) == 0)
        {
            double usd;
            if (sscanf(input + 12, "%lf", &usd) == 1)
                trade(&wallet, &ledger, 0, usd / price, price);
            historyCount = 0;
        }
        else if (strcmp(input, "balance") == 0)
        {
            printBalance(&wallet, price);
            historyCount = 0;
        }
        else if (strcmp(input, "history") == 0)
        {
            printHistory(&ledger);
            historyCount++;
            if (historyCount >= 2)
                break;
        }
        else
        {
            printf("Unknown command.\n");
            historyCount = 0;
        }
    }

    printf("\nFinal Balance:\n");
    printBalance(&wallet, price);
    return 0;
}

