#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <time.h>

#define MAX_HISTORY 100
#define START_USD 75000

typedef struct
{
    char ticker[4];
    double btc;
    double usd;
} Wallet;
typedef struct
{
    char entries[MAX_HISTORY][200];
    int count;
} Ledger;

typedef enum
{
    BUY,
    SELL
} TradeType;
typedef enum
{
    AOUNT_BTC,
    AMOUNT_USD
} AmountType;
// ---------- UTIL ----------
int getLivePrice(void)
{
    return (rand() % (35000 - 25000 + 1)) + 25000;
}
void getTimestamp(char *buffer, )
{
    time_t now = time(NULL);
    struct tm *t = localtime(&now);
    strftime(buffer, 64, "%B %d, %Y %H:%M:%S", t);
}
// ---------- WALLET ----------
void printBalance(Wallet *w, int price)
{
    printf("USD: $%.2f | BTC: %.4f | Value: $%.2f\n",
           w->usd, w->btc, w->btc * price);
}
// ---------- LEDGER ----------
void addHistory(Ledger *l, const char *msg)
{
    if (l->count < MAX_HISTORY)
    {
        strcpy(l->entries[l->count++], msg);
    }
}
void printHistory(Ledger *l)
{
    if (l->count == 0)
    {
        printf("No transactions.\n");
        return;
    }
    for (int i = 0; i < l->count; i++)
    {
        printf("%s\n", l->entries[i]);
    }
}
// ---------- TRADING ----------
void buy(Wallet *w, Ledger *l, double value, int price)
{
    double btcAmount;
    if (value > 10)
    { // USD-based buy
        btcAmount = value / price;
    }
    else
    { // BTC-based buy
        btcAmount = value;
    }
    if (w->usd < btcAmount * price)
    {
        printf("Not enough USD.\n");
        return;
    }
    w->usd -= btcAmount * price;
    w->btc += btcAmount;
    char timebuf[64], log[200];
    getTimestamp(timebuf);
    sprintf(log, "Bought %.4f BTC on %s", btcAmount, timebuf);
    addHistory(l, log);
}

void buyByValueUsd(Wallet *w, Ledger *l, double value, int price)
{
    double btcAmount;
    btcAmount = value / price;
    if (w->usd < value)
    {
        printf("Not enough USD.\n");
        return;
    }
    w->usd -= value;
    w->btc += btcAmount;
    char timebuf[64], log[200];
    getTimestamp(timebuf);
    sprintf(log, "Bought %.4f BTC on %s", btcAmount, timebuf);
    addHistory(l, log);
}

void sell(Wallet *w, Ledger *l, double value, int price)
{
    double btcAmount;
    if (value > 10)
    { // USD-based sell
        btcAmount = value / price;
    }
    else
    { // BTC-based sell
        btcAmount = value;
    }
    if (w->btc < btcAmount)
    {
        printf("Not enough BTC.\n");
        return;
    }
    w->btc -= btcAmount;
    w->usd += btcAmount * price;
    char timebuf[64], log[200];
    getTimestamp(timebuf);
    sprintf(log, "Sold %.4f BTC on %s", btcAmount, timebuf);
    addHistory(l, log);
}
