#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <string.h>

#define ROWS 12
#define COLS 12
#define SHADOW_CAP 500

void print_grid(int lifeGrid[ROWS][COLS], int generationIndex)
{
    printf("Generation %d:\n", generationIndex);
    for (int rowIndex = 0; rowIndex < ROWS; rowIndex++)
    {
        for (int colIndex = 0; colIndex < COLS; colIndex++)
            printf("%c ", lifeGrid[rowIndex][colIndex] ? 'X' : 'O');
        printf("\n");
    }
    printf("\n");
}

int count_alive_cells(int lifeGrid[ROWS][COLS])
{
    int aliveCount = 0;
    for (int rowIndex = 0; rowIndex < ROWS; rowIndex++)
        for (int colIndex = 0; colIndex < COLS; colIndex++)
            aliveCount += lifeGrid[rowIndex][colIndex];
    return aliveCount;
}

void copy_grid(int destinationGrid[ROWS][COLS], int sourceGrid[ROWS][COLS])
{
    for (int rowIndex = 0; rowIndex < ROWS; rowIndex++)
        for (int colIndex = 0; colIndex < COLS; colIndex++)
            destinationGrid[rowIndex][colIndex] = sourceGrid[rowIndex][colIndex];
}

int read_input_line(char *inputBuffer, int bufferSize)
{
    if (!fgets(inputBuffer, bufferSize, stdin))
        return 0;

    size_t length = strlen(inputBuffer);
    if (length && inputBuffer[length - 1] == '\n')
        inputBuffer[length - 1] = '\0';

    return 1;
}

int is_exit_command(const char *inputString)
{
    return strncmp(inputString, "exit", 4) == 0 ||
           strncmp(inputString, "EXIT", 4) == 0;
}

int step_life(int currentGrid[ROWS][COLS],
              int nextGrid[ROWS][COLS],
              int *changedCellCount)
{
    int localChangedCount = 0;

    for (int rowIndex = 0; rowIndex < ROWS; rowIndex++)
    {
        for (int colIndex = 0; colIndex < COLS; colIndex++)
        {
            int neighborCount = 0;

            for (int deltaRow = -1; deltaRow <= 1; deltaRow++)
                for (int deltaCol = -1; deltaCol <= 1; deltaCol++)
                    if (!(deltaRow == 0 && deltaCol == 0))
                        neighborCount +=
                            currentGrid[(rowIndex + deltaRow + ROWS) % ROWS]
                                       [(colIndex + deltaCol + COLS) % COLS];

            int nextCellState =
                currentGrid[rowIndex][colIndex]
                    ? (neighborCount == 2 || neighborCount == 3)
                    : (neighborCount == 3);

            nextGrid[rowIndex][colIndex] = nextCellState;

            if (nextCellState != currentGrid[rowIndex][colIndex])
                localChangedCount++;
        }
    }

    *changedCellCount = localChangedCount;
    return localChangedCount == 0;
}

int shadow_find_stability_generation(int startingGrid[ROWS][COLS])
{
    int shadowCurrentGrid[ROWS][COLS];
    int shadowNextGrid[ROWS][COLS];

    copy_grid(shadowCurrentGrid, startingGrid);

    for (int generationIndex = 1; generationIndex <= SHADOW_CAP; generationIndex++)
    {
        int changedCellCount = 0;
        int isStable = step_life(shadowCurrentGrid, shadowNextGrid, &changedCellCount);
        copy_grid(shadowCurrentGrid, shadowNextGrid);

        if (isStable)
            return generationIndex;
    }

    return -1;
}

int main(void)
{
    srand((unsigned)time(NULL));

    int originalGrid[ROWS][COLS];
    int currentGrid[ROWS][COLS];
    int nextGrid[ROWS][COLS];

    for (int rowIndex = 0; rowIndex < ROWS; rowIndex++)
        for (int colIndex = 0; colIndex < COLS; colIndex++)
            originalGrid[rowIndex][colIndex] = rand() % 2;

    int stabilityGeneration = shadow_find_stability_generation(originalGrid);
    int stabilityHorizon = (stabilityGeneration != -1) ? stabilityGeneration : SHADOW_CAP;

    int earlyEndGeneration = stabilityHorizon / 3;
    int midEndGeneration = (2 * stabilityHorizon) / 3;
    int lateEndGeneration = stabilityHorizon;

    if (earlyEndGeneration < 1)
        earlyEndGeneration = 1;
    if (midEndGeneration < earlyEndGeneration)
        midEndGeneration = earlyEndGeneration;
    if (lateEndGeneration < midEndGeneration)
        lateEndGeneration = midEndGeneration;

    copy_grid(currentGrid, originalGrid);

    int totalCellCount = ROWS * COLS;
    int initialAliveCount = count_alive_cells(currentGrid);

    printf("\nInitial State:\n");
    printf("Alive: %d / %d (%.2f%%)\n\n",
           initialAliveCount,
           totalCellCount,
           initialAliveCount * 100.0 / totalCellCount);

    printf("BET OPTIONS:\n");
    printf("1) EARLY\n");
    printf("2) MID\n");
    printf("3) LATE\n");
    printf("4) NEVER\n");

    printf("\nChoose 1/2/3/4 (or type 'exit'): ");

    char userInput[64];
    if (!read_input_line(userInput, sizeof(userInput)))
        return 1;
    if (is_exit_command(userInput))
        return 0;

    int playerGuessBucket = atoi(userInput);
    if (playerGuessBucket < 1 || playerGuessBucket > 4)
    {
        printf("Invalid choice.\n");
        return 1;
    }

    int targetGeneration =
        (playerGuessBucket == 1) ? earlyEndGeneration : (playerGuessBucket == 2) ? midEndGeneration
                                                    : (playerGuessBucket == 3)   ? lateEndGeneration
                                                                                 : SHADOW_CAP;

    print_grid(currentGrid, 0);

    int reachedGeneration = 0;
    int gridIsStable = 0;

    for (int generationIndex = 1; generationIndex <= targetGeneration; generationIndex++)
    {
        int changedCellCount = 0;
        gridIsStable = step_life(currentGrid, nextGrid, &changedCellCount);
        copy_grid(currentGrid, nextGrid);
        reachedGeneration = generationIndex;

        if (generationIndex == earlyEndGeneration ||
            generationIndex == midEndGeneration ||
            generationIndex == lateEndGeneration ||
            generationIndex == targetGeneration)
            print_grid(currentGrid, generationIndex);

        if (gridIsStable)
            break;
    }

    int actualBucket = 4;
    if (stabilityGeneration != -1)
    {
        if (stabilityGeneration <= earlyEndGeneration)
            actualBucket = 1;
        else if (stabilityGeneration <= midEndGeneration)
            actualBucket = 2;
        else
            actualBucket = 3;
    }

    int finalAliveCount = count_alive_cells(currentGrid);
    int finalDeadCount = totalCellCount - finalAliveCount;

    printf("Final counts at generation %d:\n", reachedGeneration);
    printf("Alive (X): %d\n", finalAliveCount);
    printf("Dead  (O): %d\n", finalDeadCount);

    if (stabilityGeneration == -1)
        printf("Stability: NOT FOUND within %d generations.\n", SHADOW_CAP);
    else
        printf("Stability: reached at generation %d.\n", stabilityGeneration);

    if (playerGuessBucket == actualBucket)
        printf("You were RIGHT.\n");
    else
        printf("You were WRONG.\n");

    return 0;
}

