        printf("\n");                              // Extra newline for spacing between records.
        l = l->next;                               // Move to the next node.
    }
}

// Function to trim leading and trailing whitespace from a string.
// Note: This function modifies the original string in place and returns a pointer
// to the first non-whitespace character.
char *trimwhitespace(char *str)
{
    char *end;

    if (str == NULL)
        return NULL; // Handle NULL input string.

    // Trim leading space: advance 'str' pointer past any leading whitespace characters.
    while (isspace((unsigned char)*str))
        str++;

    if (*str == 0) // If the string is all spaces or empty after trimming leading spaces.
        return str;

    // Trim trailing space: find the end of the string, then move 'end' backwards
    // as long as it's a whitespace character and still after 'str'.
    end = str + strlen(str) - 1;
    while (end > str && isspace((unsigned char)*end))
        end--;

    // Write new null terminator character: effectively truncates the string after
    // the last non-whitespace character.
    end[1] = '\0';

    return str; // Return the pointer to the start of the trimmed string.
}

// Function to add a new Person record to the end of the linked list.
// head: The current head pointer of the linked list.
// name, age, city: Data for the new person record.
// Returns the (potentially updated) head of the linked list.
Candy *addrecord(Candy *head, char name[], int calories, int fat_grams)
{

    Candy *record = NULL;
    // Allocate memory for the new Person structure.
    record = (Candy *)malloc(sizeof(struct Candy));
    if (record == NULL)
    { // Check if malloc failed.
        perror("Failed to allocate memory for new record");
        exit(EXIT_FAILURE);
    }

    // Copy the input data into the new record's fields.
    strcpy(record->name, name);
    record->calories = calories;
    record->fat_grams = fat_grams;
    record->next = NULL; // Initialize 'next' pointer to NULL, as it's the new last element.

    // Add the new record to the list.
    if (head == NULL)
    {                  // Case 1: The list is currently empty.
        head = record; // The new record becomes the head.
        return head;
    }
    else
    { // Case 2: The list is not empty; find the last node and append.
        Candy *current = head;
        while (current->next != NULL) // Traverse until 'current' is the last node.
            current = current->next;

        // Add our new guy on to the last guy in the list.
        current->next = record;
    }

    return head; // Return the head of the list (may be the original head or the new record if it was the first).
}

