{

    Candy *head = NULL; // Initialize the head of the linked list to NULL (empty).

    // Open the CSV file in read mode ("r").
    // IMPORTANT: You need to change the file path to match the location on your system.
    // Use double backslashes (\\) in Windows paths, or forward slashes (/) for Linux/macOS.
    FILE *fp = fopen("/Volumes/Trev Media/10_codeLibrary/C_++/50_CSc/assignments/assignemt7/mycandy.txt", "r");

    // Check if the file was opened successfully.
    if (fp == NULL)
    {
        perror("Unable to open file!"); // Print error message if opening fails.
        exit(1);                        // Exit the program with an error code.
    }

    char chunk[256]; // Buffer to hold one line of data read from the file.
    fgets(chunk, sizeof(chunk), fp);

    // Read the file line by line using fgets.
    // fgets reads up to sizeof(chunk)-1 characters or until a newline.
    while (fgets(chunk, sizeof(chunk), fp) != NULL)
    {
        char name[256];    // Buffer to store extracted name.
        int calories = -1; // Variable to store extracted age (initialized to -1 as a default/error indicator).
        int fat_grams;     // Buffer to store extracted city.

        // Use strtok to tokenize the line by comma delimiter.
        // strtok modifies 'chunk', so make sure to copy fields if needed.
        char *pt;                // Pointer to store the token.
        pt = strtok(chunk, ","); // Get the first token (name).

        // Trim whitespace from the first token.
        pt = trimwhitespace(pt);
        int count = 0; // Counter to keep track of which field is being parsed.

        while (pt != NULL)
        { // Loop through all tokens on the line.
            if (count == 0)
                strcpy(name, pt); // First token is name.
            if (count == 1)
                calories = atoi(pt); // Second token is age (convert to int).
            if (count == 2)
                fat_grams = atoi(pt);

            pt = strtok(NULL, ",");  // Get the next token (pass NULL to continue tokenizing the same string).
            pt = trimwhitespace(pt); // Trim whitespace from the next token.
            count++;                 // Increment field counter.
        }
        // After parsing, create a record and add it to the linked list.
        head = addrecord(head, name, calories, fat_grams);
    }

    fclose(fp); // Close the file after reading.

    printlist(head); // Print the entire linked list to verify data.
    head = addrecord(head, "Heath Bar", 200, 16);
    printlist(head);
    // In a complete program, you would also free the memory allocated for the linked list.
}

