#include <stdio.h>
#include <stdbool.h>
#include <ctype.h>

int WordCount(char *sentence)
{
    int count = 0;
    bool inWord = false;
    while (*sentence)
    {
        if (isspace(*sentence))
        {
            inWord = false;
        }
        else if (!inWord)
        {
            count++;
            inWord = true;
        }
        sentence++;
    }
    return count;
}

int main(void)
{
    char sentence[1000];
    printf("\nWord Count Program\n");
    printf("Enter a sentence: ");
    fgets(sentence, sizeof(sentence), stdin); // read entire line
    int wordCount = WordCount(sentence);
    printf("Number of words: %d\n", wordCount);
    printf("\n");
    return 0;
}

