#include <stdio.h>
#include <string.h>
#include <stdlib.h>

void switchFirstChars(char *input)
{
    char first[50], last[50];
    int result = sscanf(input, "%s %s", first, last);

    if (result == 2) // Restricts it to only work with two words
    {
        char temp = first[0]; // Swaps the first character of each word and stores it in a temp buffer
        first[0] = last[0];   // Takes the first character of the 2nd word and stores it in the now empty 1st character of the first word
        last[0] = temp;       // Takes the first character of the 1st word and stores it in the now empty 1st character of the second word

        char result[100];                      // Creates a new string called result
        sprintf(result, "%s %s", first, last); // Stores formatted string into result as a buffer
        printf("%s\n", result);                // Prints the result string
    }
    else
    {
        printf("Invalid input\n"); // Prints invalid input if the input is not two words
    }
}

