#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Computes missing numbers from 1 to max in userNumbers[]
// Stores missing numbers in missinguserNumbers[] and returns count
int missing(int userNumbers[], int size, int missingNumbers[])
{
    int N = userNumbers[0];
    for (int i = 1; i < size; i++)
        if (userNumbers[i] > N)
            N = userNumbers[i];

    int count = 0;
    for (int num = 1; num <= N; num++)
    {
        int found = 0;
        for (int i = 0; i < size; i++)
            if (userNumbers[i] == num)
                found = 1;
        if (!found)
            missingNumbers[count++] = num;
    }
    return count;
}

int main(void)
{
    char line[5000];
    int userNumbers[1000], missingNumbers[1000];
    int size = 0;

    // Get input
    printf("\n");
    printf("Enter numbers: ");
    fgets(line, sizeof(line), stdin);

    char *token = strtok(line, " \n,");
    while (token != NULL && size < 1000)
    {
        userNumbers[size++] = atoi(token);
        token = strtok(NULL, " \n,");
    }

    if (size == 0)
    {
        printf("No numbers entered.\n");
        return 0;
    }
    if (size == 1)
    {
        printf("Only one number entered.\n");
        return 0;
    }
    // Compute missing numbers
    int missingCount = missing(userNumbers, size, missingNumbers);

    // Print results
    printf("Numbers Missing Between 1 - %d: ", size + 1);
    for (int i = 0; i < missingCount; i++)
        printf("%d ", missingNumbers[i]);
    printf("\n");

    return 0;
}

// Problem 10:
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_INPUT 1000

// Computes missing numbers between 1 and N
// userNumbers[]: input array
// size: number of elements in userNumbers
// missingNumbers[]: array to fill with missing numbers
// returns count of missing numbers, and sets *N_ptr to maximum number
int missing(int userNumbers[], int size, int missingNumbers[], int *N_ptr)
{
    if (size <= 0)
    {
        *N_ptr = 0;
        return 0;
    }

    int N = userNumbers[0];
    for (int i = 1; i < size; i++)
        if (userNumbers[i] > N)
            N = userNumbers[i];

    *N_ptr = N; // Pass max back to main

    int count = 0;
    for (int num = 1; num <= N; num++)
    {
        int found = 0;
        for (int i = 0; i < size; i++)
            if (userNumbers[i] == num)
            {
                found = 1;
                break;
            }
        if (!found)
            missingNumbers[count++] = num;
    }

    return count;
}

int main(void)
{
    char line[MAX_INPUT];
    int userNumbers[MAX_INPUT];
    int missingNumbers[MAX_INPUT];
    int size = 0;
    do
    {

        printf("\nEnter Set Of Numbers:  "); //
        fgets(line, sizeof(line), stdin);

        char *token = strtok(line, " ,\n");
        while (token != NULL && size < MAX_INPUT)
        {
            userNumbers[size++] = atoi(token);
            token = strtok(NULL, " ,\n");
        }

        if (size == 0)
        {
            printf("No numbers entered. Try again.\n");
            size = 0;
        }
        else if (size == 1)
        {
            printf("At least two numbers are required. Try again\n");
            size = 0;
        }
    } while (size < 2);
    printf("\n");
    int N;
    int missingCount = missing(userNumbers, size, missingNumbers, &N);

    if (missingCount == 0)
        printf("No missing numbers.\n");
    else
    {
        printf("Range: 1 to %d\n", N);
        printf("\n");
        printf("Number of Missing Integers: %d\n", missingCount);

        printf("Integers Missing: ");
        for (int i = 0; i < missingCount; i++)
            printf("%d ", missingNumbers[i]);
        printf("\n");
    }
    printf("\n");
    return 0;
}

