// Problem 12
#include <stdio.h>

void decimalToBinary(int n)
{
    if (n == 0)
    {
        printf("0");
        return;
    }

    int binary[32]; // enough for 32-bit integer
    int i = 0;

    while (n > 0)
    {
        binary[i] = n % 2; // store remainder
        n = n / 2;
        i++;
    }

    // print in reverse order
    for (int j = i - 1; j >= 0; j--)
    {
        printf("%d", binary[j]);
    }
}

int main(void)
{
    int nums[] = {13, 7, 22};
    int size = 3;
    printf("\n");
    printf("Decimal to Binary Converter\n");
    printf("----------------------------\n");
    printf("Sample Inputs: 13, 7, 22\n");
    printf("\n");
    for (int i = 0; i < size; i++)
    {
        printf("Decimal %d → Binary ", nums[i]);
        decimalToBinary(nums[i]);
        printf("\n");
    }
    printf("\n");

    return 0;
}
