#include <stdio.h>
#include <stdlib.h>

typedef struct
{
    int *arr;
    int size;
    int min;
    int max;
    int minRank;
    int maxRank;
} MinMax;

void min_max(MinMax *list)
{
    list->min = list->arr[0];
    list->max = list->arr[0];
    list->minRank = 1;
    list->maxRank = 1;

    for (int i = 1; i < list->size; i++)
    {
        if (list->arr[i] <= list->min)
        { // <= ensures last occurrence
            list->min = list->arr[i];
            list->minRank = i + 1; // 1-based rank
        }
        if (list->arr[i] >= list->max)
        { // >= ensures last occurrence
            list->max = list->arr[i];
            list->maxRank = i + 1;
        }
    }
}

int main(void)
{
    int values[] = {5, 2, 8, 1, 7, 1, 8};
    int size = sizeof(values) / sizeof(values[0]);

    MinMax list = {values, size, 0, 0, 0, 0};

    min_max(&list);

    printf("The minimum value is %d and the rank is %d in the list\n", list.min, list.minRank);
    printf("The maximum value is %d and the rank is %d in the list\n", list.max, list.maxRank);

    return 0;
}

