#include <stdio.h>
#include <stdlib.h>

typedef struct
{
    double *height;
    double *weight;
    double *bmi;
    int length;
} BMIData;

// Function to calculate BMI array dynamically
void calculateBMI(double height[], double weight[], double bmi[], int length)
{
    for (int i = 0; i < length; i++)
    {
        bmi[i] = weight[i] / (height[i] * height[i]);
    }
}

// Function to print BMI table
void printBMITable(BMIData data)
{
    printf("\nBody Mass Index Table\n\n");
    printf("Height\tWeight\tBMI\n");
    printf("---------------------\n");
    for (int i = 0; i < data.length; i++)
    {
        printf("%.2f\t%.2f\t%.2f\n", data.height[i], data.weight[i], data.bmi[i]);
    }
    printf("\n");
}

int main(void)
{
    double height[] = {1.47, 1.5, 1.52, 1.55, 1.57, 1.6, 1.63, 1.65, 1.68, 1.7,
                       1.73, 1.75, 1.78, 1.8, 1.83};
    double weight[] = {52.21, 53.12, 54.48, 55.84, 57.2, 58.57, 59.93, 61.29, 63.11, 64.47,
                       66.28, 68.1, 69.92, 72.19, 74.46};

    int length = sizeof(height) / sizeof(height[0]);
    double bmi[length]; // dynamically allocate BMI array

    // Calculate BMI dynamically
    calculateBMI(height, weight, bmi, length);

    // Store in BMIData struct
    BMIData data = {height, weight, bmi, length};

    // Print table
    printBMITable(data);

    return 0;
}

