#include <stdio.h>
#include <stdlib.h>
#include <time.h>

// Function to calculate BMI array
void calculateBMI(double height[], double weight[], double bmi[], int length)
{
    for (int i = 0; i < length; i++)
    {
        bmi[i] = weight[i] / (height[i] * height[i]);
    }
}

// Function to calculate the mean of a random sample from an array
double get_sample_mean(double data[], int size, int sample_size)
{
    double sum = 0;
    for (int i = 0; i < sample_size; i++)
    {
        int random_index = rand() % size;
        sum += data[random_index];
    }
    return sum / sample_size;
}

int main(void)
{
    srand(time(NULL)); // Seed random generator

    double height[] = {1.47, 1.5, 1.52, 1.55, 1.57, 1.6, 1.63, 1.65, 1.68, 1.7};
    double weight[] = {52.21, 53.12, 54.48, 55.84, 57.2, 58.57, 59.93, 61.29, 63.11, 64.47};
    int length = sizeof(height) / sizeof(height[0]);

    double bmi[length];

    // Calculate BMI in a separate function
    calculateBMI(height, weight, bmi, length);

    // Print sample means

    printf("\n");
    printf("Sample mean of height: %.2f\n", get_sample_mean(height, length, 5));
    printf("Sample mean of weight: %.2f\n", get_sample_mean(weight, length, 5));
    printf("Sample mean of BMI: %.2f\n", get_sample_mean(bmi, length, 5));
    printf("\n");

    return 0;
}

