#include <stdio.h>
#include <stdlib.h>

typedef struct
{
    int x;
    int y;
} Coord;

// Pass by copy (won't modify original)
void wontchange(Coord c)
{
    c.x += 100;
    c.y += 1000;
}

// Pass by address (will modify original)
void willchange(Coord *c)
{
    c->x += 100;
    c->y += 1000;
}

int main(void)
{
    Coord point;
    point.x = 2;
    point.y = 3;

    // Call wontchange (pass by copy)
    wontchange(point);
    printf("After wontchange: x = %d, y = %d\n", point.x, point.y);
    // Output: x = 2, y = 3

    // Call willchange (pass by address)
    willchange(&point);
    printf("After willchange: x = %d, y = %d\n", point.x, point.y);
    // Output: x = 102, y = 1003

    return 0;
}
