void readysetgo(int *raceResults, int n) // Shuffles the raceResults array into a random order
{
    static int init = 0;
    time_t t;

    if (!init)
    {
        srand((unsigned)time(&t));
        init = 1;
    }

    for (int i = 0; i < n; i++)
