        if (!fgets(line, sizeof line, stdin))
        {
            // If fgets fails (EOF), break the loop.
            break;
        }

        // Parse command + up to 3 integers.
        int parsed = sscanf(line, "%19s %d %d %d",
                            command, &betOrder[0], &betOrder[1], &betOrder[2]);

        // =======================
        // STRING MENU BRANCHES
        // =======================

        if (strcmp(command, "balance") == 0 || strcmp(command, "Balance") == 0)
        {
            // [T2] right now this still subtracts a bet; see money logic in TODO list
            printf("Current Balance: $%.2f\n", balance);
        }
        else if (strcmp(command, "exacta") == 0 || strcmp(command, "Exacta") == 0)
        {
            betType = 2;
            balance -= bet;
            payout = bet * 10;
            balance += payout;
            printf("New Balance: $%.2f\n", balance);
        }
        else if (strcmp(command, "exactabox") == 0 ||
                 strcmp(command, "ExactaBox") == 0 ||
                 strcmp(command, "exactaBox") == 0)
        {
            betType = 2;
            balance -= bet;
            payout = bet * 10;
            balance += payout;
            printf("New Balance: $%.2f\n", balance);
            // [T4] validate parsed + picks, then call exactabox() and update balance
        }
        else if (strcmp(command, "trifecta") == 0 || strcmp(command, "Trifecta") == 0)
        {
            betType = 3;
            balance -= bet;
            payout = bet * 12.5;
            balance += payout;
            printf("New Balance: $%.2f\n", balance);
            // [T5] validate parsed + picks, then call trifecta() and update balance
        }
        else if (strcmp(command, "trifectabox") == 0 ||
                 strcmp(command, "TrifectaBox") == 0 ||
                 strcmp(command, "trifectaBox") == 0)
        {
            betType = 3;
            balance -= bet;
            payout = bet * 10;
            balance += payout;
            printf("New Balance: $%.2f\n", balance);
        }
        else if (strcmp(command, "exit") == 0 || strcmp(command, "Exit") == 0)
        {
            printf("Exiting the program.\n");
            // loop will stop because of the while-condition below
        }
        else
        {
            printf("Invalid command. Please try again.\n");
            // (refund behavior here would be optional, so not flagged)
        }

    } while (strcmp(command, "exit") != 0 && strcmp(command, "Exit") != 0);

    return 0;
}

